import React from "react";
import { Button } from "../button";
import { InputText } from "../input-text";
import { addSprite } from "../../store/store";
import styles from "./upload-image.module.scss";

export const UploadImage = React.memo(function UploadImage() {
  const [spriteName, setSpriteName] = React.useState<string>("");
  const [spriteUrl, setSpriteUrl] = React.useState<string>("");

  const handleChangeName = React.useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setSpriteName(e.target.value);
    },
    []
  );

  const handleUploadImage = React.useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      const files = e.target.files;
      if (files) {
        const sprite = files[0];
        console.log(files[0]);
        setSpriteUrl(URL.createObjectURL(sprite));
      }
    },
    []
  );

  const sendSpriteData = React.useCallback(() => {
    console.log(spriteName, spriteUrl);
  }, [spriteName, spriteUrl]);

  return (
    <div className={styles.uploadImage}>
      <InputText label="Enter name of sprite" onChange={handleChangeName} />
      <input
        type="file"
        accept="image/png, image/gif, image/jpeg"
        onChange={handleUploadImage}
      />
      <Button
        onClick={sendSpriteData}
        text="send"
        disabled={!spriteName.length || !spriteUrl.length}
      />
    </div>
  );
});
